    static final DHParameters draft_ffdhe3072;
    private static final String draft_ffdhe3072_p = "";
    static final DHParameters draft_ffdhe4096;
    private static final String draft_ffdhe4096_p = "";
    static final DHParameters draft_ffdhe6144;
    private static final String draft_ffdhe6144_p = "";
    static final DHParameters draft_ffdhe8192;
    private static final String draft_ffdhe8192_p = "";
    
    static {
        TWO = BigInteger.valueOf(2L);
        EXT_negotiated_ff_dhe_groups = Integers.valueOf(101);
        draft_ffdhe2432 = fromSafeP("");
        draft_ffdhe3072 = fromSafeP("");
        draft_ffdhe4096 = fromSafeP("");
        draft_ffdhe6144 = fromSafeP("");
        draft_ffdhe8192 = fromSafeP("");
    }
    
    public static void addNegotiatedDHEGroupsClientExtension(final Hashtable hashtable, final short[] array) throws IOException {
        hashtable.put(TlsDHUtils.EXT_negotiated_ff_dhe_groups, createNegotiatedDHEGroupsClientExtension(array));
    }
    
    public static void addNegotiatedDHEGroupsServerExtension(final Hashtable hashtable, final short n) throws IOException {
        hashtable.put(TlsDHUtils.EXT_negotiated_ff_dhe_groups, createNegotiatedDHEGroupsServerExtension(n));
    }
    
    public static boolean areCompatibleParameters(final DHParameters dhParameters, final DHParameters dhParameters2) {
        return dhParameters.getP().equals(dhParameters2.getP()) && dhParameters.getG().equals(dhParameters2.getG()) && (dhParameters.getQ() == null || dhParameters2.getQ() == null || dhParameters.getQ().equals(dhParameters2.getQ()));
    }
    
    public static byte[] calculateDHBasicAgreement(final DHPublicKeyParameters dhPublicKeyParameters, final DHPrivateKeyParameters dhPrivateKeyParameters) {
        final DHBasicAgreement dhBasicAgreement = new DHBasicAgreement();
        dhBasicAgreement.init(dhPrivateKeyParameters);
        return BigIntegers.asUnsignedByteArray(dhBasicAgreement.calculateAgreement(dhPublicKeyParameters));
    }
    
    public static boolean containsDHECipherSuites(final int[] array) {
        for (int i = 0; i < array.length; ++i) {
            if (isDHECipherSuite(array[i])) {
                return true;
            }
        }
        return false;
    }
    
    public static byte[] createNegotiatedDHEGroupsClientExtension(final short[] array) throws IOException {
        if (array != null && array.length >= 1 && array.length <= 255) {
            return TlsUtils.encodeUint8ArrayWithUint8Length(array);
        }
        throw new TlsFatalAlert((short)80);
    }
    
    public static byte[] createNegotiatedDHEGroupsServerExtension(final short n) throws IOException {
        TlsUtils.checkUint8(n);
        final byte[] array = { 0 };
        TlsUtils.writeUint8(n, array, 0);
        return array;
    }
    
    private static BigInteger fromHex(final String s) {
        return new BigInteger(1, Hex.decode(s));
    }
    
    private static DHParameters fromSafeP(final String s) {
        final BigInteger fromHex = fromHex(s);
        return new DHParameters(fromHex, TlsDHUtils.TWO, fromHex.shiftRight(1));
    }
    
    public static AsymmetricCipherKeyPair generateDHKeyPair(final SecureRandom secureRandom, final DHParameters dhParameters) {
        final DHBasicKeyPairGenerator dhBasicKeyPairGenerator = new DHBasicKeyPairGenerator();
        dhBasicKeyPairGenerator.init(new DHKeyGenerationParameters(secureRandom, dhParameters));
        return dhBasicKeyPairGenerator.generateKeyPair();
    }
    
    public static DHPrivateKeyParameters generateEphemeralClientKeyExchange(final SecureRandom secureRandom, final DHParameters dhParameters, final OutputStream outputStream) throws IOException {
        final AsymmetricCipherKeyPair generateDHKeyPair = generateDHKeyPair(secureRandom, dhParameters);
        writeDHParameter(((DHPublicKeyParameters)generateDHKeyPair.getPublic()).getY(), outputStream);
        return (DHPrivateKeyParameters)generateDHKeyPair.getPrivate();
    }
    
    public static DHPrivateKeyParameters generateEphemeralServerKeyExchange(final SecureRandom secureRandom, final DHParameters dhParameters, final OutputStream outputStream) throws IOException {
        final AsymmetricCipherKeyPair generateDHKeyPair = generateDHKeyPair(secureRandom, dhParameters);
        new ServerDHParams((DHPublicKeyParameters)generateDHKeyPair.getPublic()).encode(outputStream);
        return (DHPrivateKeyParameters)generateDHKeyPair.getPrivate();
    }
    
    public static short[] getNegotiatedDHEGroupsClientExtension(final Hashtable hashtable) throws IOException {
        final byte[] extensionData = TlsUtils.getExtensionData(hashtable, TlsDHUtils.EXT_negotiated_ff_dhe_groups);
        if (extensionData == null) {
            return null;
        }
        return readNegotiatedDHEGroupsClientExtension(extensionData);
    }
    
    public static short getNegotiatedDHEGroupsServerExtension(final Hashtable hashtable) throws IOException {
        final byte[] extensionData = TlsUtils.getExtensionData(hashtable, TlsDHUtils.EXT_negotiated_ff_dhe_groups);
        if (extensionData == null) {
            return -1;
        }
        return readNegotiatedDHEGroupsServerExtension(extensionData);
    }
    
    public static DHParameters getParametersForDHEGroup(final short n) {
        if (n == 0) {
            return TlsDHUtils.draft_ffdhe2432;
        }
        if (n == 1) {
            return TlsDHUtils.draft_ffdhe3072;
        }
        if (n == 2) {
            return TlsDHUtils.draft_ffdhe4096;
        }
        if (n == 3) {
            return TlsDHUtils.draft_ffdhe6144;
        }
        if (n != 4) {
            return null;
        }
        return TlsDHUtils.draft_ffdhe8192;
    }
    
